export default {
	TRACKED_PLAYERS : "TRACKED_PLAYERS",
	PLAYERS_DATA : "PLAYERS_DATA",
	CURRENT_PLAYER : "CURRENT_PLAYER",
	IS_CURRENT_PLAYER_VALID : "IS_CURRENT_PLAYER_VALID",


	addPlayerToTrack: async () => {

		if (appsmith.store[trackedPlayerStore.TRACKED_PLAYERS] === undefined){
			await storeValue(trackedPlayerStore.TRACKED_PLAYERS, [])
		}

		var toAdd = titledPlayers.triggeredRow.name
		if (toAdd !== undefined){
			var playerset = new Set(appsmith.store[trackedPlayerStore.TRACKED_PLAYERS]);
			playerset.add(toAdd)
			await storeValue(trackedPlayerStore.TRACKED_PLAYERS, Array.from(playerset))
		}
	},

	findPlayerFromDbOrApi : async () => {
		var ps_d = appsmith.store[trackedPlayerStore.PLAYERS_DATA]
		var curr_p = appsmith.store[trackedPlayerStore.CURRENT_PLAYER]
		if (ps_d === undefined){
			ps_d = {}
			var res1 = await chess_com_player.run().catch(() => {

			})
			ps_d[curr_p] = res1

			await storeValue(trackedPlayerStore.PLAYERS_DATA, ps_d)
		} else {
			if ( curr_p in ps_d) {

				return ps_d[curr_p]
			} else {
				var res = await chess_com_player.run().catch(() => {

				})
				ps_d[curr_p] = res
				await storeValue(trackedPlayerStore.PLAYERS_DATA, ps_d)
			}
		}
		return appsmith.store[trackedPlayerStore.PLAYERS_DATA][curr_p]

	},

	isValidPlayer : async() => {
		var temp = await trackedPlayerStore.findPlayerFromDbOrApi()
		var curr_p = appsmith.store[trackedPlayerStore.CURRENT_PLAYER]
		var res =  temp !== undefined && appsmith.store[trackedPlayerStore.PLAYERS_DATA][curr_p] !== undefined
		if (res === true) {
			await storeValue(trackedPlayerStore.IS_CURRENT_PLAYER_VALID, true)
		} else {
			await storeValue(trackedPlayerStore.IS_CURRENT_PLAYER_VALID, false)
		}
		return res
	},

	updatePlayerOnKeyPresss : async () => {
		await storeValue(trackedPlayerStore.CURRENT_PLAYER, playerToTrack.text)
		await trackedPlayerStore.isValidPlayer()
	},




	addPlayerToTrackFromInput: async () => {

		if (appsmith.store[trackedPlayerStore.TRACKED_PLAYERS] === undefined){
			await storeValue(trackedPlayerStore.TRACKED_PLAYERS, [])
		}

		var toAdd = playerToTrack.text
		if (toAdd !== undefined){
			var playerset = new Set(appsmith.store[trackedPlayerStore.TRACKED_PLAYERS]);
			playerset.add(toAdd)
			await storeValue(trackedPlayerStore.TRACKED_PLAYERS, Array.from(playerset))
		}

	},
}
