jsonPathKeys:
async () => {
  await storeValue(trackedPlayerStore.PLAYER_GAMES_LOADING, true);
  var playername = trackedPlayerStore.getPlayerToView();
  var res;
  if (playername !== undefined) {
    await storeValue(trackedPlayerStore.CURRENT_PLAYER, playername);
    res = await chess_com_player_month_games.run();
  }
  if (res === undefined || res.length === 0 || res.games.length === 0) {
    await storeValue(trackedPlayerStore.CURRENT_PLAYER_NOW_MONTH_GAMES, undefined);
    await storeValue(trackedPlayerStore.PLAYER_GAMES_LOADING, false);
    return undefined;
  }
  await storeValue(trackedPlayerStore.CURRENT_PLAYER_NOW_MONTH_GAMES, res.games.reverse());
  await storeValue(trackedPlayerStore.PLAYER_GAMES_LOADING, false);
  return res.games.reverse();
}
