export default {
	TRACKED_PLAYERS : "TRACKED_PLAYERS",
	PLAYERS_DATA : "PLAYERS_DATA",
	CURRENT_PLAYER : "CURRENT_PLAYER",
	IS_CURRENT_PLAYER_VALID : "IS_CURRENT_PLAYER_VALID",

	getTableTransformedData : () => {

		var res = appsmith.store[trackedPlayerStore.TRACKED_PLAYERS]
		var tabledata = []
		for(let i=0;i < res.length;i++){
			var data = {}
			data["name"] = res[i]
			data["action"] = ""
			tabledata.push(data);
		}

		return tabledata
	},

	removePlayerToTrack: async () => {

		if (appsmith.store[trackedPlayerStore.TRACKED_PLAYERS] === undefined){
			await storeValue(trackedPlayerStore.TRACKED_PLAYERS, [])
		}

		var toRemove = trackedPlayerTable.triggeredRow.name
		if (toRemove !== undefined){
			var playerset = new Set(appsmith.store[trackedPlayerStore.TRACKED_PLAYERS]);
			playerset.delete(toRemove)
			await storeValue(trackedPlayerStore.TRACKED_PLAYERS, Array.from(playerset))
		}
	},

	getPlayerToView :  () => {
		var players =  appsmith.store[trackedPlayerStore.TRACKED_PLAYERS] 
		if(players === undefined || players.length === 0){
			return undefined
		}
		return trackedPlayerTable.selectedRow === undefined ? players[0] : trackedPlayerTable.selectedRow.name
	},

	getPlayerToViewData :  () => {
		var playername = trackedPlayerStore.getPlayerToView()
		if (playername !== undefined){
			return appsmith.store[trackedPlayerStore.PLAYERS_DATA][playername]
		}
	}
}
